INSERT INTO `wo_shelve` (`goods_id`, `user_id`, `buy_id`, `buy_time`, `goods_name`, `goods_detail`, `a_price`, `b_price`, `seller_tel`, `seller_qq`, `img_id`, `trade_way`, `class_first`, `class_second`, `is_Sale`, `shelve_time`) VALUES
(2, 2, 0, 0, '无价之宝', '这是一个无价之宝，亲，买回去吧哈哈哈，哈哈哈哈', 100, 1, 13880256590, 422483459, 9, 1, 1, 1, 2, 1331385734),
(3, 4, 0, 0, '《女友》杂志', '《女友》往期杂志，让你体验美丽之旅，，超低的价格，快来抢购吧！', 1, 6, 18782940541, 1229668446, 10, 1, 2, 5, 2, 1331387977),
(4, 4, 0, 0, '《女友》杂志', '《女友》校园版往期杂志，让你体验美丽之旅，超低的价格，快来抢购吧！', 1, 6, 18782940541, 1229668446, 11, 1, 2, 5, 2, 1331388459),
(5, 6, 0, 0, '水杯', '水杯大小适中，属半新产品（买来一年但是一直没用），杯身有漫画图案，一面是女孩，一面是男孩，充满了青春的浪漫。', 10, 15, 15202836385, 178020084, 12, 3, 3, 5, 2, 1331553712),
(6, 2, 0, 0, '王长喜 6级试题', '本人有，王长喜的英语6级模拟考试 试题，由于目前还没有做，全新哦，又没有时间去做，丢掉可惜，如果有意要者，课便宜处理，记得联系我，祝你早日过6级', 5, 25, 13880156590, 422483459, 13, 3, 2, 1, 2, 1331563614),
(7, 2, 0, 0, '装饰  子弹壳做的飞机模型', '这是军训时，在部队买的纪念品，非常精辟，全是用子弹壳做的，做一个装饰很好用的，放在书桌旁边，甚是美观，由于近期缺钱，顾不得不忍痛割爱，希望有收藏爱好者，前来围观，购买', 5, 30, 13880156590, 422483459, 14, 3, 5, 1, 2, 1331564010),
(8, 2, 0, 0, '《怪诞行为学》', '享誉世界最畅销的书之一，《怪诞行为学》，很是金典，希望感兴趣者前来收藏。', 10, 54, 13880156590, 422483459, 15, 1, 2, 5, 2, 1331564252),
(9, 29, 0, 0, '女士包', '这个包包是我姐姐在一年以前送给我的，我很喜欢这个包包，它是黑色的，属于罗马风格，设计简单大方，彰显女生时尚气息，现在由于我的柜子容量太小，没地方储存，所以不得已拿出来买，有意者可以面谈，价钱不是问题，只要你喜欢什么都好说。非诚勿扰。谢谢，呵呵', 10, 45, 18782975237, 569098903, 16, 3, 4, 4, 2, 1331702557);
