(1, 1, 1, '好人！<img alt="微笑" src="/Public/xheditor/xheditor_emot/default/smile.gif" />', 1331202385),
(2, 2, 1, '不自觉的人，一个学生 还想当老师！！！！！！', 1331205236),
(3, 2, 1, '<img alt="微笑" src="/Public/xheditor/xheditor_emot/default/smile.gif" />，支持张老师，张教主哈', 1331220292),
(4, 2, 2, '蒋老师和谐 是个好老师', 1331631881),
(5, 2, 2, '好险，做年的英语给我打了60分，差点就挂了，蒋老师和谐，威武！！', 1331632645),
(6, 1, 2, '谢谢英语老师每次都给我60几分,让我逃脱了!', 1331650082),
(7, 1, 1, '@<strong>Ginchenorlee 啥时候成张教主了?</strong>', 1331650144);
