INSERT INTO `wo_weitie` (`weitie_id`, `user_id`, `title`, `content`, `forum_class_first`, `forum_class_second`, `count_comment`, `is_photo`, `iscomment`, `put`, `read_times`, `last_comment_user_id`, `last_comment_is_read`, `ctime`) VALUES
(2, 3, '一场春雨一场热，恭喜窝窝园，正式上线测试', '<p>To： @Noon团队</p><p>希望你们越走越远，戒骄戒躁，路很长，你们当下做的不错，come on !</p><p>看到你们，就想起我们当年的激情......</p><p style="text-align: right;">From：@yoyiorlee</p>', 2, 4, 5, 0, 1, 1, 26, 2, 0, 1331201780),
(4, 2, 'NOON 团队 感谢信', '<p>&nbsp; &nbsp; 今天是个奇迹的时刻</p><p>在这里 &nbsp; 窝窝园 进过一半年的打拼 终于上线了</p><p>在这里首先感谢我们一直在前线奋斗的工作人员</p><p>再次感谢我哥，谢谢你，谢谢你的一直在默默的支持</p><p>谢谢 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; FROM：NOON</p><p><br /></p>', 2, 4, 3, 0, 1, 4, 28, 1, 0, 1331202087),
(6, 15, '冷的解决办法', '<p>&nbsp; &nbsp; 有一天，小明觉得很冷，告诉爸爸：“好冷啊！”。小明爸爸说：“到墙角去”！为什么呢？？？</p><p><br /></p><p><br /></p><p>&nbsp; &nbsp; &nbsp;墙角90度！！！！</p>', 5, 3, 4, 0, 1, 2, 18, 2, 1, 1331212527),
(7, 2, '同一篇帖子，在新浪，天涯，猫扑，腾讯的不同回复', '&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img src="http://ww1.sinaimg.cn/bmiddle/6e5c06b1jw1dqx2xg9p39j.jpg" align="middle" width="550" height="3700" alt="" />&nbsp;&nbsp;&nbsp;&nbsp;', 5, 3, 0, 1, 1, 1, 1, 0, 0, 1331553558),
(8, 2, '硅谷称之为“常事”的网站设计原则', '<br /><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 18px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; border-image: initial; font: inherit; vertical-align: baseline; line-height: 26px; color: rgb(68, 68, 68); font-size: 16px; font-family: ''Microsoft Yahei'', ''Helvetica Neue'', ''Luxi Sans'', ''DejaVu Sans'', Tahoma, ''Hiragino Sans GB'', STHeiti; ">初级创业者应该搞清楚保持细致与步入主流的区别。以下是网站设计过程中的几个原则，硅谷的人也常不屑地称其为“常识（Normals）”。</p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 18px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; border-image: initial; font: inherit; vertical-align: baseline; line-height: 26px; color: rgb(68, 68, 68); font-size: 16px; font-family: ''Microsoft Yahei'', ''Helvetica Neue'', ''Luxi Sans'', ''DejaVu Sans'', Tahoma, ''Hiragino Sans GB'', STHeiti; "><span style="color: rgb(24, 24, 24); font-size: 2.1rem; line-height: 1.25; ">怎样介绍网站</span></p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 18px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; border-image: initial; font: inherit; vertical-align: baseline; line-height: 26px; color: rgb(68, 68, 68); font-size: 16px; font-family: ''Microsoft Yahei'', ''Helvetica Neue'', ''Luxi Sans'', ''DejaVu Sans'', Tahoma, ''Hiragino Sans GB'', STHeiti; ">不用告诉用户你网站的“原理（how it works）”或“是什么（what it is）”，当然也无需告诉他们你的公司有多奇妙。你需要抓住重点，完整而明确地告诉用户你提供什么样的服务。新用户需要知道的是，你提供的服务在他们的生活中能起到什么作用。</p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 18px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; border-image: initial; font: inherit; vertical-align: baseline; line-height: 26px; color: rgb(68, 68, 68); font-size: 16px; font-family: ''Microsoft Yahei'', ''Helvetica Neue'', ''Luxi Sans'', ''DejaVu Sans'', Tahoma, ''Hiragino Sans GB'', STHeiti; ">看一下Twitter主页是怎么向新用户介绍的。很简洁，“欢迎来到 Twitter。与你关心的人们一起，探寻变化的大千世界”。Facebook也类似，“联结你我，分享生活，尽在Facebook”。非常有才！现在初级创业者应该知道，采用明确的方式告诉用户什么时候使用、为什么有用。比如通过前面两句话我们就能知道，Twitter是用来探寻世界的变化，Facebook则是用来沟通和分享。</p><h4 style="margin-top: 0px; margin-right: 0px; margin-bottom: 3px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; border-image: initial; font: inherit; vertical-align: baseline; color: rgb(24, 24, 24); line-height: 1.25; font-size: 2.1rem; font-family: ''Microsoft Yahei'', ''Helvetica Neue'', ''Luxi Sans'', ''DejaVu Sans'', Tahoma, ''Hiragino Sans GB'', STHeiti; ">确定呈现内容</h4><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 18px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; border-image: initial; font: inherit; vertical-align: baseline; line-height: 26px; color: rgb(68, 68, 68); font-size: 16px; font-family: ''Microsoft Yahei'', ''Helvetica Neue'', ''Luxi Sans'', ''DejaVu Sans'', Tahoma, ''Hiragino Sans GB'', STHeiti; ">我们知道你可能会这样想：“Facebook与Twitter当然能用时髦语句来介绍自己，因为新用户在去他们的网站前，就已经知道他们的网站是做什么的”。没错，这也是问题的关键。现在的大部分人都是从他人那里听说某个网站，而你的工作就是满足这些人对你网站的期望，服务当然也是越快速、越令人信服越好。</p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 18px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; border-image: initial; font: inherit; vertical-align: baseline; line-height: 26px; color: rgb(68, 68, 68); font-size: 16px; font-family: ''Microsoft Yahei'', ''Helvetica Neue'', ''Luxi Sans'', ''DejaVu Sans'', Tahoma, ''Hiragino Sans GB'', STHeiti; ">怎样才能让新用户爱上你的网站？ 这就需要给他们带来一段难忘的初始体验。建立一个引导式的过程，让他们感觉像是在进行愉快的散步，这样用户就会很高兴地了解当前服务能为他们做些什么。</p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 18px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; border-image: initial; font: inherit; vertical-align: baseline; line-height: 26px; color: rgb(68, 68, 68); font-size: 16px; font-family: ''Microsoft Yahei'', ''Helvetica Neue'', ''Luxi Sans'', ''DejaVu Sans'', Tahoma, ''Hiragino Sans GB'', STHeiti; ">太多的网站在新用户注册后便甩手不管，期望他们自己去摸索和发现下一步该做什么。相反，应该把新用户看成正与你的网站约会，接着通过一个优雅、直观、使人愉悦的引导过程，让他们了解网站的操作流程。对初级创业者来说，学会开始、驾驭并让使用体验个性化都是极其重要的。初级创业者应该正确对待错误，不应害怕冒险或固化行动 ，措施应该灵活且可以在必要时撤消。</p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 18px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; border-image: initial; font: inherit; vertical-align: baseline; line-height: 26px; color: rgb(68, 68, 68); font-size: 16px; font-family: ''Microsoft Yahei'', ''Helvetica Neue'', ''Luxi Sans'', ''DejaVu Sans'', Tahoma, ''Hiragino Sans GB'', STHeiti; ">例如，Twitter在引导用户使用这个服务的同时，也会询问他们的兴趣，然后立即利用这些信息为其创造个性化体验。完成这一过程后，无关的应用将被关闭，这样用户的面板上就只会显示那些自己可能感兴趣的功能选项。</p><h4 style="margin-top: 0px; margin-right: 0px; margin-bottom: 3px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; border-image: initial; font: inherit; vertical-align: baseline; color: rgb(24, 24, 24); line-height: 1.25; font-size: 2.1rem; font-family: ''Microsoft Yahei'', ''Helvetica Neue'', ''Luxi Sans'', ''DejaVu Sans'', Tahoma, ''Hiragino Sans GB'', STHeiti; ">化繁为简</h4><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 18px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; border-image: initial; font: inherit; vertical-align: baseline; line-height: 26px; color: rgb(68, 68, 68); font-size: 16px; font-family: ''Microsoft Yahei'', ''Helvetica Neue'', ''Luxi Sans'', ''DejaVu Sans'', Tahoma, ''Hiragino Sans GB'', STHeiti; ">针对特定用户进行产品设计的关键是满足他们的特别需求。即使你的目标用户不是网络新人，当没有经验的游客来访问你的网站时，也要确保他们能有收获。直观的界面能让每个用户都喜欢，还要能够预测用户的下一步操作。让产品步入主流，它必须要简单，还能满足用户需求。用户往往不关心你的技术能做什么，而只关心你的服务能帮他们完成什么。</p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 18px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; border-image: initial; font: inherit; vertical-align: baseline; line-height: 26px; color: rgb(68, 68, 68); font-size: 16px; font-family: ''Microsoft Yahei'', ''Helvetica Neue'', ''Luxi Sans'', ''DejaVu Sans'', Tahoma, ''Hiragino Sans GB'', STHeiti; ">还有大量尚开发的机遇等着那些能化繁为简的企业家去开发。如果不知是什么，或许你的爷爷奶奶也能给你带来启发。</p>', 2, 2, 2, 0, 1, 3, 14, 1, 0, 1331553949),
(9, 2, '也许上辈子是人类吧！', '&nbsp;&nbsp;<span style="font-style: normal; color: rgb(119, 119, 119); font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20px; background-color: rgb(243, 243, 243); ">在澳大利亚的一个海滩上，一名游客突然发现了这只抑郁的袋鼠，它在海边站立了好久，于是拿起相机拍摄，谁知看到的竟是让他都觉得不可思议的这一 幕。。事后，据动物学家介绍，动物有时也会患上抑郁症，有些也会选择自杀的方式了结此生。这样一只动情的袋鼠，也许上辈子是人类吧..<span style="font-style: normal; color: rgb(119, 119, 119); font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20px; background-color: rgb(243, 243, 243); "><span style="font-style: normal; color: rgb(119, 119, 119); font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 20px; background-color: rgb(243, 243, 243); ">子是人类吧...</span></span></span><img src="http://ww1.sinaimg.cn/bmiddle/7839a605jw1dqwpxcfdefj.jpg" width="500" height="3500" alt="" />', 5, 4, 0, 1, 1, 0, 3, 0, 0, 1331554144),
